import {
	Paper,
	Table,
	TableBody,
	TableCell,
	TableContainer,
	TableHead,
	TableRow,
} from '@mui/material'
import React, { useEffect, useState } from 'react'


const DataTable = ({ cellsData, cellsName }) => {

	const [searchText, setSearchText] = useState('')

	const [itemData, setItemData] = useState({
		id: '',
		name: '',
		cost: '',
		addTime: '',
	})

	useEffect(()=>{
		const fetchData = async () => {
		const url = 'http://localhost:8080/api/v1/users/tradeitems'
		const searchOption = {
			name: searchText,
		}

			const requestOptions = {
				method: 'GET',
				headers: { 'Content-Type': 'application/json' },
				body: JSON.stringify(searchOption),
				}
			

			
			try {
				const response = await fetch(url, requestOptions)
				if (!response.ok) {
					throw new Error('Failed to fetch items data')
				}
				const data = await response.json()
				setItemData({
					id: data.id,
					name: data.name,
					cost: data.cost,
					addTime: data.addTime
				})
			} catch (error) {
				console.error(error)
			}
		}
		fetchData()
	}, [])

	const { id, name, addTime, cost } = itemData

	return (
		<div>
			<TableContainer component={Paper}>
				<Table sx={{ minWidth: 650 }} aria-label='simple table'>
					<TableHead>
						<TableRow>
							{cellsName.map((name, index) => (
								<TableCell key={name} align={index === 0 ? 'left' : 'right'}>
									{name}
								</TableCell>
							))}
						</TableRow>
					</TableHead>
					<TableBody>
							<TableRow>
								<TableCell align='right'>{id}</TableCell>
								<TableCell align='right'>{name}</TableCell>
								<TableCell align='right'>{addTime}</TableCell>
								<TableCell align='right'>{cost}</TableCell>
							</TableRow>
					</TableBody>
				</Table>
			</TableContainer>
		</div>
	)
}

export default DataTable
