import {
	Paper,
	Table,
	TableBody,
	TableCell,
	TableContainer,
	TableHead,
	TableRow,
	Button,
  } from '@mui/material';
  import React, { useEffect, useState } from 'react';
  
  
  const ProductTable = ({ cellsData, cellsName }) => {
	const [itemData, setItemData] = useState([]);
	const [order, setOrder] = useState([]);
  
	useEffect(() => {
		const fetchData = async () => {
		  const url = 'http://localhost:8080/api/pizza/product';
		  try {
			const response = await fetch(url);
			if (!response.ok) {
			  throw new Error('Failed to fetch items data');
			}
			const data = await response.json();
			setItemData(data);
		  } catch (error) {
			console.error(error);
		  }
		};
		fetchData();
	  }, []);
	
	  // Function to add a product to the order
	  const addToOrder = (item) => {
		setOrder([...order, item]);
	  };

	  const removeFromOrder = (item) => {
		setOrder(order.filter((i) => i.id !== item.id));
	  };
	
	  return (
		<div>
		  <TableContainer component={Paper}>
			<Table sx={{ minWidth: 650 }} aria-label="simple table">
			  <TableHead>
				<TableRow>
				  {cellsName.map((name, index) => (
					<TableCell key={name} align="center">
					  {name}
					</TableCell>
				  ))}
				  <TableCell align="center">Action</TableCell>
				</TableRow>
			  </TableHead>
			  <TableBody>
				{itemData.map((item) => (
				  <TableRow key={item.id}>
					<TableCell align="center">{item.id}</TableCell>
					<TableCell align="center">{item.name}</TableCell>
					<TableCell align="center">{item.price}</TableCell>
					<TableCell align="center">{item.description}</TableCell>
					<TableCell align="center">{item.imageId}</TableCell>
					<TableCell align="center">{item.categoryName}</TableCell>
					<TableCell align="center">
					  <button onClick={() => addToOrder(item)}>Add to Order</button>
					</TableCell>
				  </TableRow>
				))}
			  </TableBody>
			</Table>
		  </TableContainer>
		<br></br>
		<div className='max-w-7xl mx-auto px-4 sm:px-6 lg:px-8' >
        <h2>Your Order:</h2>
        {order.length === 0 ? (
          <p>Your order is empty.</p>
        ) : (
          <div className='max-w-7xl mx-auto px-4 sm:px-6 lg:px-8'>
            {order.map((item, index) => (
              <div key={index}>
                <span>
                  {item.name} - ${item.price}
                </span>
                <Button
                  variant="contained"
                  color="secondary"
                  onClick={() => removeFromOrder(item)}
                >
                  Remove
                </Button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
	  );
	};
	
	export default ProductTable;
